htmlspecialchars(trim($_POST['email']), ENT_QUOTES);

echo 'Nome: ' .
htmlentities(trim($_POST['nome']));

echo 'E-mail: ' .
htmlentities(trim($_POST['email']));

echo 'Nome: ' .
htmlspecialchars(addslashes(trim($_POST['nome'])));

echo 'E-mail: ' .
htmlentities(addslashes(trim($_POST['email'])));

endif;

?>

